import React, { useState } from 'react';
import axios from 'axios';

const AddProduct = () => {
  const [name, setName] = useState('');
  const [quantity, setQuantity] = useState('');
  const [price, setPrice] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Submitting form:', { name, quantity, price });

    try {
      const response = await axios.post('http://localhost:5002/api/products', {
        name,
        quantity,
        price
      }, {
        headers: {
          'Content-Type': 'application/json',
          // 'Authorization': `Bearer ${yourToken}` // Если требуется токен
        }
      });

      console.log(response.data);
      setMessage('Product added successfully');
    } catch (error) {
      console.error('Error adding product:', error);
      if (error.response) {
        setMessage(`Error adding product: ${error.response.data.message}`);
      } else if (error.request) {
        setMessage('Error adding product: Network error');
      } else {
        setMessage(`Error adding product: ${error.message}`);
      }
    }
  };

  return (
    <div>
      <h2>Add New Product</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Product Name:</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="quantity">Quantity:</label>
          <input
            type="number"
            id="quantity"
            value={quantity}
            onChange={(e) => setQuantity(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="price">Price:</label>
          <input
            type="text"
            id="price"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            required
          />
        </div>
        <button type="submit">Add Product</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default AddProduct;
