// src/Components/AdminPanel.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../Styles/AdminPanel.css';

const AdminPanel = () => {
  const [products, setProducts] = useState([]);
  const [productName, setProductName] = useState('');
  const [productQuantity, setProductQuantity] = useState('');
  const [productPrice, setProductPrice] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:5002/api/products');
        setProducts(response.data);
      } catch (error) {
        console.error('Ошибка загрузки продуктов:', error);
      }
    };

    fetchProducts();
  }, []);

  const handleAddProduct = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5002/api/products', {
        name: productName,
        quantity: productQuantity,
        price: productPrice
      });
      setMessage('Продукт добавлен успешно');
      // Обновляем список продуктов
      const response = await axios.get('http://localhost:5002/api/products');
      setProducts(response.data);
      // Очистка формы
      setProductName('');
      setProductQuantity('');
      setProductPrice('');
    } catch (error) {
      console.error('Ошибка добавления продукта:', error);
      setMessage('Ошибка добавления продукта');
    }
  };

  const handleDeleteProduct = async (productId) => {
    try {
      await axios.delete(`http://localhost:5002/api/products/${productId}`);
      setProducts(products.filter(product => product._id !== productId));
      setMessage('Продукт удален успешно');
    } catch (error) {
      console.error('Ошибка удаления продукта:', error);
      setMessage('Ошибка удаления продукта');
    }
  };

  return (
    <div className="admin-panel">
      <h2>Административная панель</h2>
      <form onSubmit={handleAddProduct}>
        <div>
          <label htmlFor="name">Название:</label>
          <input
            type="text"
            id="name"
            value={productName}
            onChange={(e) => setProductName(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="quantity">Количество:</label>
          <input
            type="number"
            id="quantity"
            value={productQuantity}
            onChange={(e) => setProductQuantity(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="price">Цена:</label>
          <input
            type="number"
            id="price"
            value={productPrice}
            onChange={(e) => setProductPrice(e.target.value)}
            required
          />
        </div>
        <button type="submit">Добавить продукт</button>
      </form>
      {message && <p>{message}</p>}
      <h3>Список продуктов</h3>
      <ul>
        {products.map(product => (
          <li key={product._id}>
            {product.name} - {product.quantity} - {product.price}₸
            <button onClick={() => handleDeleteProduct(product._id)}>Удалить</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminPanel;
