import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Импортируем useNavigate

const AdminPanel = () => {
  const [name, setName] = useState('');
  const [quantity, setQuantity] = useState('');
  const [price, setPrice] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate(); // Инициализируем useNavigate

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Submitting form:', { name, quantity, price });

    try {
      const response = await axios.post('http://localhost:5002/api/products', {
        name,
        quantity: Number(quantity), // Конвертируем в число
        price: Number(price)        // Конвертируем в число
      });
      console.log('Response:', response.data);
      setMessage('Product added successfully');
      // Очистка формы после успешного добавления
      setName('');
      setQuantity('');
      setPrice('');
    } catch (err) {
      console.error('Error adding product', err);

      if (err.response) {
        setMessage(`Error adding product: ${err.response.data.message}`);
      } else if (err.request) {
        setMessage('Error adding product: Network error');
      } else {
        setMessage(`Error adding product: ${err.message}`);
      }
    }
  };

  const goToRegister = () => {
    navigate('/register'); // Перенаправление на страницу регистрации
  };

  return (
    <div>
      <h1>Admin Panel</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="quantity">Quantity:</label>
          <input
            type="number"
            id="quantity"
            value={quantity}
            onChange={(e) => setQuantity(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="price">Price:</label>
          <input
            type="number"
            id="price"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            required
          />
        </div>
        <button type="submit">Add Product</button>
      </form>
      <button onClick={goToRegister} className="register-button">Register</button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default AdminPanel;
