import React, { useState } from 'react';
import axios from 'axios';

const AddProduct = () => {
  const [name, setName] = useState('');
  const [quantity, setQuantity] = useState('');
  const [price, setPrice] = useState('');
  const [products, setProducts] = useState([]);
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name || !quantity || !price) {
      setMessage('Все поля обязательны для заполнения.');
      return;
    }

    try {
      // Отправляем данные на сервер
      const response = await axios.post('http://localhost:5002/api/products', {
        name,
        quantity,
        price
      });

      // Добавляем продукт в таблицу
      setProducts([...products, response.data]);

      // Очищаем форму
      setName('');
      setQuantity('');
      setPrice('');

      setMessage('Продукт успешно добавлен!');
    } catch (error) {
      console.error('Error adding product:', error);
      setMessage('Ошибка при добавлении продукта. Попробуйте снова.');
    }
  };

  return (
    <div>
      <h2>Добавить новый продукт</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Название продукта:</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="quantity">Количество:</label>
          <input
            type="number"
            id="quantity"
            value={quantity}
            onChange={(e) => setQuantity(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="price">Цена:</label>
          <input
            type="text"
            id="price"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            required
          />
        </div>
        <button type="submit">Добавить продукт</button>
      </form>

      {/* Отображение сообщения об успехе или ошибке */}
      {message && <p>{message}</p>}

      {/* Отображение продуктов в таблице */}
      {products.length > 0 && (
        <div>
          <h3>Список продуктов</h3>
          <table>
            <thead>
              <tr>
                <th>Название</th>
                <th>Количество</th>
                <th>Цена</th>
              </tr>
            </thead>
            <tbody>
              {products.map((product, index) => (
                <tr key={index}>
                  <td>{product.name}</td>
                  <td>{product.quantity}</td>
                  <td>{product.price}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default AddProduct;
