import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios'; // Убедитесь, что axios установлен
import '../Styles/HomePage.css'; 

const LoginForm = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5002/login', {
        username: email, // Используйте email как username
        password,
      });

      // Сохраните токен в localStorage
      localStorage.setItem('token', response.data.token);

      // Уведомите родительский компонент, если нужно
      onLogin && onLogin(email);

      // Перенаправьте пользователя на страницу аккаунта
      navigate('/account');
    } catch (error) {
      alert('Invalid email or password');
    }
  };

  return (
    <div className="login-page">
      <h2 className="login-page-text">Вход</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Пароль:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Вход</button>
      </form>
      <p className="register-link">
        Еще нет аккаунта? <Link to="/register">Регистрация</Link>
      </p>
    </div>
  );
};

export default LoginForm;
