import React, { useEffect, useState } from 'react';
import '../Styles/CartPage.css'; // Убедитесь, что файл CSS существует и правильный

const CartPage = ({ cart, setCart }) => {
  const [totalSum, setTotalSum] = useState(0);

  // Функция для вычисления общей суммы
  const calculateTotalSum = () => {
    // Убедитесь, что все цены числовые
    const sum = cart.reduce((acc, item) => acc + parseFloat(item.price.replace('₸', '').replace(' ', '')), 0);
    setTotalSum(sum);
  };

  // Вызываем calculateTotalSum при изменении cart
  useEffect(() => {
    calculateTotalSum();
  }, [cart]);

  // Функция для удаления товара из корзины
  const removeFromCart = (index) => {
    const updatedCart = cart.filter((_, i) => i !== index);
    setCart(updatedCart);
  };

  return (
    <div className="cart-page">
      <h2>Корзина</h2>
      <div className="cart-list">
        {cart.length > 0 ? (
          cart.map((item, index) => (
            <div key={index} className="cart-item">
              <img src={item.image} alt={item.name} />
              <p>{item.name} - {item.price}</p>
              <button className="delete-button" onClick={() => removeFromCart(index)}>✕</button>
            </div>
          ))
        ) : (
          <p>Ваша корзина пуста</p>
        )}
      </div>
      <div className="order-summary">
        <h3>Сумма заказа</h3>
        <p>Стоимость продуктов: <span>{totalSum.toLocaleString()} ₸</span></p>
        <p>Итого: <span>{totalSum.toLocaleString()} ₸</span></p>
        <button className="order-button">Оформить заказ</button>
      </div>
    </div>
  );
};

export default CartPage;
