import React, { useState } from 'react';
import { useParams, useNavigate} from 'react-router-dom';
import '../Styles/ProductDetails.css';

const ProductCard = ({ addToFavorites, addToCart }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('description');

  const products = [
    { id: 1, name: 'Помада-стик', price: '3 048 ₸', description: 'INFLUENCE BEAUTY influence. Взгляд на классику из будущего. Этот продукт - уникальное сочетание всех лучших качеств помады-стика. Инновационная формула CONJUNCTION MIX отвечает за ее основные свойства: полуматовый финиш, стойкость, высокую пигментацию и равномерное нанесение с первого раза. Помада увлажняет и разглаживает губы. Полупрозрачный корпус, через который виден весь механизм помады, подчеркивает ее лаконичность и функциональность.', image: 'https://pcdn.goldapple.ru/p/p/19000008522/web/696d674d61696e8dad7d552b7407dfullhd.webp', composition: 'RICINUS COMMUNIS (CASTOR) SEED OIL, OCTYLDODECANOL, ETHYLHEXYL PALMITATE, OZOKERITE, PARAFFIN, ISONONYL ISONONANOATE, BIS-DIGLYCERYL POLYACYLADIPATE-2, CAPRYLIC/CAPRIC TRIGLYCERIDE, SYNTHETIC FLUORPHLOGOPITE, SILICA DIMETHYL SILYLATE, COPERNICIA CERIFERA CERA [COPERNICA CERIFERA (CARNAUBA) WAX], AROMA (FLAVOR), LIMONENE, HEXYL CINNAMAL, GLYCOL MONTANATE, SHOREA ROBUSTA RESIN, METHYLPARABEN, PROPYLPARABEN, BHT, CI 77891 (TITANIUM DIOXIDE), CI 17200 (RED 33 LAKE), CI 15850:1 (RED 7), CI 45410 (RED 28 LAKE), CI 42090 (BLUE 1 LAKE), CI 77499 (IRON OXIDES).Состав: масло ши, витамин Е, минеральные масла, натуральные красители.', application: 'Наносите обычным способом или кистью на кожу губ.' },
    { id: 2, name: 'Тушь для ресниц', price: '5 850 ₸', description: 'DARLING* lash cocoon. Я твоя ТЕРМОСТОЙКАЯ тушь, которая мягко заворачивает каждую ресничку в "КОКОН", устойчивый к любым внешним воздействиям: слезы, пот, дождь, даже бассейн не смогут лишить ваши глаза ВЫРАЗИТЕЛЬНОСТИ. Волшебство исчезнет только при воздействии теплой воды от 38°C, без использования дополнительных средств. Неужели мы создали идеальную тушь?', image: 'https://pcdn.goldapple.ru/p/p/19000047864/web/696d674d61696e8dc4cb8bde73e4ffullhd.webp',  composition: ' Water, Styrene/Acrylates/Ammonium Methacrylate Copolymer, Iron Oxides (CI 77499), Butylene Glycol, Alcohol Denat., Pullulan, Acrylates Copolymer, Polyacrylate-13, Silica, Hydroxyethyl Acrylate/Sodium Acryloyldimethyl Taurate Copolymer, Polyisobutene, Phenoxyethanol, Polysorbate 80, Sodium Dehydroacetate, C11-15 Pareth-7, Caprylyl Glycol, Sodium Laureth-12 Sulfate, Tetrasodium EDTA, Sorbitan Isostearate, Polysorbate 20, Polysorbate 60, Ethylhexylglycerin, Potassium Sorbate, Disodium EDTA.', application: 'Взять щеточку и аккуратно нанести тушь от основания ресниц зигзагообразными движениями. Нет необходимости в очищающих средствах. Смывать теплой водой от 38°C.' },
    { id: 3, name: 'Тональный крем', price: '7 896 ₸', description: 'ARTDECO perfect teint foundation. Тональный крем для безупречной кожи Perfect Teint Foundation. Легкая, как пёрышко текстура не только не содержит минеральных масел, но и, благодаря входящим в состав нежным частицам пудры, обладает абсорбирующим свойством. Результат – лёгкий матовый финиш, текстура растворяется на коже, благодаря чему кожа выравнивается и выглядит естественно. Без эффекта маски! Тональный крем отличается своей стойкостью, средней и высокой плотностью покрытия, которая тем не менее едва заметна на коже. Небольшие неровности и выпуклости на коже можно визуально скорректировать с помощью крема Perfect Teint Foundation, а, благодаря входящей в состав воде, текстура действует освежающе на кожу. Преимущество: входящие в состав компоненты увлажняют кожу и ухаживают за ней, склонная к раздражению кожа успокаивается, а экстракт персиковых лепестков защищает кожу от вредного воздействия окружающей среды. Абсолютно идеальный тональный крем!', image: 'https://pcdn.goldapple.ru/p/p/17354600004/web/696d674d61696e8dbec0b94f12791fullhd.webp', composition: 'Aqua (Water), Cyclopentasiloxane, Cyclohexasiloxane, Isododecane, Glycerin, Cetyl PEG/PPG-10/1 Dimethicone, Microcrystalline Cellulose, Alcohol Denat., PEG-10 Dimethicone, Polyglyceryl-4 Isostearate, Copernicia Cerifera Cera, Polymnia Sonchifolia Root Juice, Euterpe Oleracea Fruit Extract, Tocopheryl Acetate, Ascorbyl Tetraisopalmitate, Sodium Hyaluronate, Alpha-Glucan Oligosaccharide, Inulin, Shorea Robusta Resin, Lactobacillus, Phenoxyethanol, DMDM Hydantoin, Ethylhexylglycerin, Potassium Sorbate, Sodium Benzoate, Sodium Chloride, Disteardimonium Hectorite, Magnesium Sulfate, Triethoxycaprylylsilane, Paraffin, Propylene Carbonate, Parfum (Fragrance), Aluminum Hydroxide, Tapioca Starch, Glycol Montanate, Maltodextrin, Sorbic Acid, Citric Acid, Limonene. +/-: CI 77891 (Titanium Dioxide), CI 77492 (Iron Oxide), CI 77491 (Iron Oxide), CI 77499 (Iron Oxide).', application: 'Пару капель тонального средства нанесите на тыльную сторону ладони, затем равномерно распределите крем на коже лица с помощью кисточки, спонжа для макияжа или пальцами. Перед применением хорошо взболтать.' },
    { id: 4, name: 'Подводка для глаз', price: '1 459 ₸', description: 'ESSENCE dip eyeliner. Bодостойкая подводка насыщенного черного цвета обеспечивает до 24 часов безупречной стойкости! Благодаря более мягкому аппликатору подводка dip eyeliner waterproof 24h long-lasting от essence обеспечивает приятное, легкое нанесение.', image: 'https://pcdn.goldapple.ru/p/p/19000239307/web/696d674d61696e5f30323533623464666435623834663830613366383233373230633264623463388dc7ca86aa5dae1fullhd.webp', composition: 'Aqua (water), propanediol, ammonium acrylates copolymer, glyceryl rosinate, acrylates/ethylhexyl acrylate copolymer, pvp, methylpropanediol, xanthan gum, ethylhexylglycerin, simethicone, caprylyl glycol, phenylpropanol, cellulose, sodium sulfate, phenoxyethanol, sodium dehydroacetate, ci 77266 (black 2) (nano), ci 77499 (iron oxides).', application: 'Подводка для глаз Dip Eyeliner. Водостойкая' },
    { id: 5, name: 'Румяна', price: '10 020 ₸', description: 'KIKO MILANO unlimited blush. Стойкие пудровые румяна с регулируемой интенсивностью. Идеально подходят для того, чтобы: - оживить цвет лица притягательным здоровым сиянием с утра до ночи. В чем их особенности? - бархатистая ультрапигментированная компактная текстура, которая добавляет цвета и сохраняет стойкость до 12 часов*; - они мгновенно растушевываются на коже, обеспечивая приятное ощущение комфорта; - их легко растушевать, чтобы создать интенсивность от легкой до насыщенной; - представлены в матовом и металлическом финише; - удобная упаковка с компактным зеркалом, чтобы поправлять макияж на ходу. Протестировано дерматологами. Некомедогенно.', image: 'https://pcdn.goldapple.ru/p/p/19000040535/web/696d674d61696e8dc2d9591fdd7bafullhd.webp', composition: 'TALC, LAUROYL LYSINE, POLYMETHYL METHACRYLATE, MICA, DIMETHICONOL STEARATE, ALUMINUM HYDROXIDE, CAPRYLIC/CAPRIC TRIGLYC ERIDE, DIMETHICONE, TRIMETHYLSILOXYSILICATE, CAPRYLYL GLYCOL, PHENOXYETHANOL, MAGNESIUM MYRISTATE, HEXYLENE GLYCOL, DIMETHICONE CROS SPOLYMER, CI 15850 (RED 7), CI 77492 - CI 77499 (IRON OXIDES), CI 19140(YELLOW 5 LAKE), CI 77891(TITANIUM DIOXIDE), (IRON OXIDES).', application: 'Нанесите румяна на скулы и виски, растушевывая к контурам лица при помощи кисти для пудры Face 10 Blush Brush.' },
    { id: 6, name: 'Консилер', price: '4 390 ₸', description: 'MAYBELLINE NEW YORK the eraser eye. Консилер для кожи вокруг глаз Maybelline The Eraser Eye быстро спрячет следы усталости, бессонных ночей, стресса и взбодрит чувствительную кожу. Освежит тусклый взгляд, добавит ему сияние. Это средство от Maybelline имеет удобный аппликатор с миниатюрным спонжем. Наносить состав легко и удобно. Формула The Eraser Eye уникальная. В нее включены экзотические компоненты. Среди них целебные ягоды годжи. Они обеспечивают активный уход за веками. Этому консилеру для кожи вокруг глаз непременно следует быть в косметичке каждой девушки.', image: 'https://pcdn.goldapple.ru/p/p/3156200011/web/696d674d61696e8dc3d6711667534fullhd.webp', composition: 'Aqua/Water/Eau, Cyclopentasiloxane, Dimethicone, Isododecane, Glycerin, Peg-9 Polydimethylsiloxyethyl Dimethicone, Butylene Glycol, Dimethicone Crosspolymer, Nylon-12, Disteardimonium Hectorite, Cyclohexasiloxane, Peg-10 Dimethicone, Cetyl Peg/Ppg-10/1 Dimethicone, Phenoxyethanol, Sodium Chloride, Polyglyceryl-4 Isostearate, Caprylyl Glycol, Disodium Stearoyl Glutamate, Ethylhexylglycerin, Methylparaben, Lycium Barbarum Fruit Extract, Chlorphenesin, Ethylparaben, Aluminum Hydroxide, Peg-9, N-Hydroxysuccinimide, Palmitoyl Oligopeptide, Chrysin, Palmitoyl Tetr Apeptide-7. [+/- May Contain/Peut Contenir: Ci 77891/Titanium Dioxide, Ci 77491, Ci 77492, Ci 77499/Iron Oxides]', application: 'Нанести на нужные зоны лица аппликатором, растушевать подушечками пальцев или спонжем. Отлично сочетается со стойкими тональными основами и пудрами серии INFAILLIBLE.' },
    { id: 7, name: 'Гель-лак для ногтей', price: '2 646 ₸', description: 'SOPHIN gel polish color.  Цветной гель-лак. Для натурального эффекта здоровых ногтей достаточно двух слоев, для плотного покрытия необходимо не менее трех слоев. Обеспечивает цветное покрытие, которое идеально наносится и быстро полимеризуется в любых видах ламп. Уникальная формула гель-лака позволяет легко создать безупречный маникюр и педикюр, который обладает эффектным глянцевым блеском, ярким цветом, потрясающей стойкостью без сколов и царапин в течение 2-3 недель. Прекрасно самовыравнивается, не имеет резкого запаха.', image: 'https://pcdn.goldapple.ru/p/p/57722100008/web/696d674d61696e8dc4a60950b2b25fullhd.webp', composition: 'Di-HEMA trimethylhexyl dicarbamate*, HEMA, Hydroxypropyl methacrylate, Trimethylbenzoyl diphenylphosphine oxide, Butyl acetate, Ethyl acetate , Isopropyl alcohol, Hydroxycyclohexyl phenyl ketone, Calcium sodium Borosilicate, Silica , Tin oxide, p-hydroxyanisole, Hydroquinone, Titanium dioxide/CI 77891, Violet 2/CI 60725', application: 'Нанесите 1-2 слоя на ногти, покрытые базой Сушите каждый слой в течение 30-40 секунд под LED лампой или 2 минуты под UV лампой Завершите покрытие топом Внимание: Избегать попадания на кожу и глаза. При попадании в глаза, промыть глаза водой в течение 15 минут. Обратиться к врачу. Вымойте кожу с мылом, чтобы удалить любое случайное загрязнение.' },
    { id: 8, name: 'Туалетная вода', price: '23 100 ₸', description: 'VERSACE eros. Уникальная аура, чувственное прикосновение, несокрушимая мужественность - таковы ключевые ноты оды любви, воспеваемой Эросом, страстным и непреклонным соблазнителем. Новый аромат олицетворяет абсолютную мужественность, соединяя в единое целое: искрящуюся ауру яркой, вибрирующей, экстраординарной свежести листьев Мяты, цедры Итальянского Лимона и Зеленого Яблока; захватывающую чувственность интригующий, восточных, обволакивающих нот Бобов Тонка, Амброксана (Амбры), цветов Герани и Ванили; соблазнительную мужественность, воплощенную в древесном шлейфе Атласского и Виргинского Кедра, Ветивера и Дубового мха, привносящих в аромат интенсивный оттенок мощи и власти.', image: 'https://pcdn.goldapple.ru/p/p/7430800010/web/696d674d61696e8dbd9320d542a1efullhd.webp', composition: 'Bерхние ноты: сицилийский апельсин. средние ноты: мед, гардения. базовые ноты: пачули, ваниль', application: 'Туалетную воду распылите на кожу с небольшого расстояния.' },
    { id: 9, name: 'Увлажняющий крем', price: '25 132 ₸', description: 'CLINIQUE for men anti-age moisturizer. Увлажняющий крем, предотвращающий появление первых признаков старения Clinique Anti-Age Moisturizer активно работает в борьбе с первыми морщинками. Напряженный ритм жизни и недостаток полноценного отдыха неумолимо отражаются на коже лица. Универсальное средство для придания коже свежего вида — крем Anti-Age Moisturizer. Активный комплекс антиоксиданты+увлажнение в составе этого средства защищает и укрепляет кожу. Также действие увлажняющего крема, предотвращающего появление первых признаков старения распространяется на защиту и укрепление верхнего и самого уязвимого слоя кожи — эпидермиса. Уникальный продукт бренда Clinique — для безупречно выглядящих мужчин.', image: 'https://pcdn.goldapple.ru/p/p/15250200038/web/696d674d61696e8dad7106a0a6345fullhd.webp', composition: 'Наносите ежедневно два раза в день на чистую кожу лица. Равномерно распределите небольшое количество средства (нажав два раза на дозатор) на всей коже лица и шеи после использования подходящего очищающего средства линии Clinique For Men. Утром). Затем нанесите солнцезащитный крем. Средство безопасно для использования на коже вокруг глаз.', application: 'Освежающий крем с шелковистой текстурой и SPF, который борется со следами усталости и первыми признаками старения.' },
    { id: 10, name: 'Шампунь', price: '3 362 ₸', description: 'LADOR moisture balancing shampoo. Увлажняющий шампунь великолепно очищает волосы и кожу головы, защищает кожу головы и придает волосам объем, блеск и шелковистость. Шампунь идеально подойдёт для чувствительной кожи головы, так как в качестве главного очищающего компонента используется мыльнянка (мыльная трава). Экстракт лаванды очищает, дезодорирует, освежает и тонизирует кожу головы, смягчает волосы, делает их более послушными. Экстракт бергамота борется с раздражением кожи головы, нормализует работу сальных желез, сохраняет чувство свежести длительное время. Экстракт фрезии обладает расслабляющим, смягчающим и успокаивающим действием. Экстракт мяты освежает и тонизирует. Экстракт ромашки помогает справиться с дерматитами кожи головы, успокаивает, придает волосам мягкость и шелковистость. Экстракт розмарина увлажняет кожу головы, помогает справиться с зудом и перхотью.', image: 'https://pcdn.goldapple.ru/p/p/19760200009/web/696d674d61696e8dc66aa10a59ebdfullhd.webp', composition: 'Water, Sodium Laureth Sulfate, Sodium Lauryl Sulfate, Cocamidopropyl Betaine, Sodium Chloride, Cocamide MEA, Glycol Distearate, Polyquaternium-10, Sodium Benzoate, Hydroxyacetophenone, Piroctone Olamine, Disodium EDTA, Citric Acid, PPG-3 Caprylyl Ether, Quaternium-80, Polyquaternium-7, Saponaria Officinalis Leaf Extract, Butylene Glycol, Acrylamidopropyltrimonium Chloride/Acrylamide Copolymer, Propylene Glycol, Lavandula Angustifolia (Lavender) Flower Extract, Monarda Didyma Leaf Extract, Mentha Piperita (Peppermint) Leaf Extract, Freesia Refracta Extract, Chamomilla Recutita (Matricaria) Flower Extract, Rosmarinus Officinalis (Rosemary) Leaf Extract, 1,2-Hexanediol, Ethylhexylglycerin, Fragrance, Alpha-Isomethyl Ionone, Citronellol, Hexyl Cinnamal.', application: 'нанести шампунь на влажные волосы, вспенить и помассировать, затем смыть теплой водой. При необходимости повторить процедуру.' },
    { id: 11, name: 'Кондиционер для волос', price: '2 217 ₸', description: 'OLLIN PROFESSIONAL restore conditioner. Защищает от УФ-повреждений, восстанавливает натуральный блеск, делает волосы более послушными, восстанавливает естественную увлажнённость и глубоко питает волосы. «Чем больше сушишь волосы, тем сильнее они становятся». NREL™ (Nano Repair EL) - инновационная технология, которая противодействует повреждениям, вызванным термовоздействием (например, сушка феном). Часть ингредиентов не проникает глубоко в слои волоса, пока он влажный, начиная реагировать только от воздействия тепла (фен, плойка). Восстановление волос изнутри NRM™ (Nano Repair Matrix) - комбинация Platinum beauty-кератинов и нано-частиц арганового масла проникает в проблемные зоны, увлажняет и восстанавливает волосы. Содержит 4 вида гиалуроновых кислот, 2 вида кератина, 5 видов керамидов, гидрогенизированные масла, гидролизованный коллаген, стволовые клетки. Без сульфатов.', image: 'https://pcdn.goldapple.ru/p/p/19760310227/web/696d674d61696e8dad7a9563f9c99fullhd.webp', composition: 'Aqua, Cetearyl Alcohol, Isopropyl Myristate, Glyceryl Stearate, Polyquaternium-7, Propylene Glycol, Ceteareth-20, Cetrimonium Chloride, Behentrimonium Chloride, Panthenol, Citric acid, Glycerin, Hydrolyzed Silk, Fragrance, Isopropyl Alcohol, Silicone Quaternium-16, Undeceth-11, Butyloctanol, Undeceth-5, Hexyl Cinnamal, Magnesium nitrate, Magnesium chloride, Methylchloroisothiazolinone, Methylisothiazolinone', application: 'Нанести на предварительно вымытые шампунем и подсушенные полотенцем волосы. Оставить на 2–3 минуты. Смыть' },
    { id: 12, name: 'Лосьон для тела', price: '6 150 ₸', description: 'MILK BAOBAB flora bouquet. Легкий и нежный лосьон для тела MILK BAOBAB с молочными протеинами и экстрактом баобаба питает и увлажняет кожу, делая её более мягкой и гладкой, а также укрепляет естественный защитный барьер кожи. Средство дарит изысканный и романтичный аромат с чарующими цветочными нотами и стойкостью более 8 часов.', image: 'https://pcdn.goldapple.ru/p/p/19000174970/web/696d674d61696e8dbb9a5a46cb4ccfullhd.webp',composition: 'Water, sodium laureth sulfate, glycerin, cocamidopropyl betaine, cocamide methyl mea, butylene glycol, milk protein extract, adansonia digitata seed extract, saponaria officinalis leaf extract, quillaja saponaria bark extract, sapindus mukurossi peel extract, freesia alba flower extract, paeonia albiflora flower extract, convallaria majalis extract, hydrangea macrophylla leaf extract, palmitoyl pentapeptide-4, copper tripeptide-1, acetyl hexapeptide-8, caprylic/capric triglyceride, sodium hyaluronate, sodium hyaluronate crosspolymer, hydrolyzed hyaluronic acid, hyaluronic acid, hydrolyzed sodium hyaluronate, betaine, panthenol, lecithin, sodium chloride, polysorbate 20, citric acid, sodium ascorbyl phosphate, 1,2-hexanediol, caprylyl glycol, sodium citrate, sodium benzoate, disodium edta, fragrance, alpha-isomethyl ionone, benzyl benzoate, benzyl salicylate, citronellol, limonene, geraniol, hexyl cinnamal, linalool.', application: 'Нанесите на влажную кожу тела, вспеньте и распределите по коже массирующими движениями, затем смойте водой.' },
